    private void Installation(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_Installation
        boolean bool=new MySQL_Database().Database_Initialization();//First, set up and initialize the MySQL database
        if(bool)
        {
            jTextArea1.setText("Installed successfully and Database is initialized!");
        }
        else
        {
            jTextArea1.setText("Installed failed! Please check if your MySQL server is on!");
        }
        try//Refresh configuration, such as the counter of ID,counter of serial number.
        {
            FileWriter fw = new FileWriter("Configuration.ini");
            BufferedWriter bw = new BufferedWriter(fw);
            bw.write("[ID]\r\n0\r\n[Serial number]\r\n0\r\n0");
            bw.close();
            fw.close();
        }
        catch(Exception e)
        {
            e.printStackTrace();
        }
    }//GEN-LAST:event_Installation

